// src/components/timetable/TimetableComponent.tsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Period {
  _id: string; // Assuming MongoDB ObjectId
  id: number;
  subject: string;
  course_code: string;
}

const TimetableComponent: React.FC = () => {
  const [periods, setPeriods] = useState<Period[]>([]);
  const [newPeriod, setNewPeriod] = useState<Partial<Period>>({ id: 0, subject: '', course_code: '' });

  useEffect(() => {
    fetchPeriods();
  }, []);

  const fetchPeriods = async () => {
    try {
      const response = await axios.get('http://localhost:5002/api/periods');
      setPeriods(response.data);
    } catch (error) {
      console.error('Error fetching periods:', error);
    }
  };

  const handleAddPeriod = async () => {
    try {
      const response = await axios.post('/api/periods', newPeriod);
      console.log('Period added:', response.data);
      setNewPeriod({ id: 0, subject: '', course_code: '' }); // Reset form after successful addition
      fetchPeriods(); // Refresh data after addition
    } catch (error) {
      console.error('Error adding period:', error);
    }
  };

  const handleUpdatePeriod = async (id: string, updatedPeriod: Partial<Period>) => {
    try {
      const response = await axios.put(`/api/periods/${id}`, updatedPeriod);
      console.log('Period updated:', response.data);
      fetchPeriods(); // Refresh data after update
    } catch (error) {
      console.error('Error updating period:', error);
    }
  };

  const handleDeletePeriod = async (id: string) => {
    try {
      const response = await axios.delete(`/api/periods/${id}`);
      console.log('Period deleted:', response.data);
      fetchPeriods(); // Refresh data after deletion
    } catch (error) {
      console.error('Error deleting period:', error);
    }
  };

  return (
    <div>
      <h2>Timetable</h2>
      <div>
        <h3>Add New Period</h3>
        <input
          type="text"
          placeholder="Subject"
          value={newPeriod.subject}
          onChange={(e) => setNewPeriod({ ...newPeriod, subject: e.target.value })}
        />
        <input
          type="text"
          placeholder="Course Code"
          value={newPeriod.course_code}
          onChange={(e) => setNewPeriod({ ...newPeriod, course_code: e.target.value })}
        />
        <button onClick={handleAddPeriod}>Add Period</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Subject</th>
            <th>Course Code</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {periods.map(period => (
            <tr key={period._id}>
              <td>{period.id}</td>
              <td>{period.subject}</td>
              <td>{period.course_code}</td>
              <td>
                <button onClick={() => handleUpdatePeriod(period._id, { subject: 'Updated Subject', course_code: 'Updated Code' })}>Update</button>
                <button onClick={() => handleDeletePeriod(period._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TimetableComponent;
